import React, { useState } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const Login = (props) => {
  const [username, setuserName] = useState("");
  const [enteredPassword, setEnteredPassword] = useState("");
  const [creds, setCreds] = useState(true);
  const [loginCon, setLoginCon] = useState(true);
  const [passLabel,setPassLabel] = useState("Password")
  const [userLabel,setUserLabel] = useState("Username")

  const emailChangeHandler = (event) => {
    setuserName(event.target.value);
  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(username, enteredPassword);
  };

  const signUpHandler = (event) => {
    event.preventDefault();
    setLoginCon(false);
    setPassLabel("Set Password");
    setUserLabel("Set Username");
  };

  const fetchSearchData = async (stDate, enDate, customQuery) => {
    const urlPath = `${url}rest/api/2/search?jql=(worklogDate >= ${stDate} AND worklogDate <= ${enDate} ${ customQuery ? `AND ${customQuery}` : '' })%20&fields=summary%2Cworklog%2Cissuetype%2Cparent%2Cproject%2Cstatus%2Cassignee%2Creporter%2Caggregatetimespent%2Ctimeoriginalestimate%2Ctimeestimate&maxResults=1000`;
    // const formattedCookies = cookies;
    const headers = new Headers();
    headers.set("Cookie", `${authCookies}`);

    try {
      const response = await fetch(urlPath, {
        method: "GET",
        headers: headers,
      });

      if (response.ok) {
        const data = await response.json();
        let resultObj = {};
        const fetchWork = data.issues.map((item) => {
          if (item.fields.worklog.total > 20) {
            fetchWorkData(item.key, resultObj, item);
          }
        });

        // setResultObj(resultObj);
        setGroupNameData(groupedByDisplayName);
      } else {
        console.error("Error fetching data from Jira:", response.statusText);
      }
    } catch (error) {
      localStorage.removeItem('cookies');
      localStorage.removeItem('url');
      localStorage.removeItem('userInfo');
      window.location.reload();
      console.error("Error fetching data from Jira:", error);
    }
  };


  return (
    <Card className={classes.login}>
      {loginCon ? <p className={classes.actions}>Login Form</p> : <p className={classes.actions}>Sign Up Form</p>}
      {!creds && (
        <div className={classes.invalidCreds}>
          <p>Invalid Credentials</p>
        </div>
      )}
      <form onSubmit={submitHandler}>
        <div className={`${classes.control}`}>
          <label htmlFor="email">{userLabel}</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={emailChangeHandler}
          />
        </div>
        <div className={`${classes.control}`}>
          <label htmlFor="password">{passLabel}</label>
          <input
            type="password"
            id="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn}>
            Login
          </Button>
          {loginCon && (
            <Button className={classes.btn} onClick={signUpHandler}>
              Sign Up
            </Button>
          )}
        </div>
      </form>
    </Card>
  );
};

export default Login;
