import React, { useState, useEffect } from "react";
import SongList from "./SongList";
import Card from "../UI/Card/Card";
import classes from "./Home.module.css";

const Home = (props) => {
  const [musicList, setMusicList] = useState([]);
  console.log(musicList);

  const fetchSearchData = async (enteredWord) => {
    const urlPath = "http://127.0.0.1:8000/api-auth/get-music";
    const headers = new Headers();
    headers.set("Content-Type", "application/json");
    headers.set("Authorization", `token ${props.token}`);
    try {
      const jsonCreds = {
        "name": enteredWord
      }
      const response = await fetch(urlPath, {
        method: "GET",
        headers: headers,
      });

      if (response.ok) {
        const data = await response.json();
        setMusicList(data);
      }
    } catch (error) {
      console.log("Error fetching data from Backend:", error);
    }
  };


  useEffect(() => {
    fetchSearchData("");
  },[]);

  console.log(musicList)

  const songsListMapped = musicList ? musicList.map((song, index) => (
    <SongList
      id={index}
      key={index}
      name={song.audio_name}
      img={song.audio_image}
      genre={song.audio_genre}
      audio={song.audio_data} />
  )) : '';

  return (
    // <Card className={classes.home}>
    //   <h1>Welcome back!</h1>
    //   <button onClick={fetchSearchData}>Check Api</button>
    // </Card>
      <Card className={classes.home}>
        <div>
          <input placeholder="Enter a song name" />
          <button>Search</button>
        </div>
        <h1>List Of Songs from Api</h1>
        <ul>{songsListMapped}</ul>
      </Card>
  );
};

export default Home;
